mapper.update:
update trip_list set trip_name = #{trip_name}, 
					 days_count = ${days_count},
					 start_date = #{start_date,jdbcType=DATE},
					 end_date = #{end_date,jdbcType=DATE},
					 trip_auth = ${trip_auth}
					 where trip_list_serial = ${trip_list_serial}

mapper.select:
select count(follow_serial)  
from follow_list
where member_id = #{member_id} 
and target_id = #{target_id}

mapper.update:
update membership set member_city = #{member_city},
				   member_web = #{member_web},
				   member_info = #{member_info}, 
				   nickname = #{nickName}
where member_id = #{member_id}

mapper.delete:
delete from follow_list
where member_id = #{member_id}
and target_id = #{target_id}

mapper.select:
select r.review_serial, r.member_id, r.place_serial, r.reputation, r.review_title, r.review_content, to_char(r.visit_date, 'rrrr"년" mm"월" dd"일"') visit_date, r.review_date, getRevCnt(r.place_serial) review_cnt from review r join follow_list f on r.member_id = f.target_id left outer join place p on p.place_serial = r.place_serial where f.member_id = #{member_id}
and p.place_serial is null
and p.place_serial is not null
