and local_code = #{_parameter}
group by place_code, local_code, a.place_serial, place_name, place_location order by place_serial

select place_code, local_code, a.place_serial, place_name, place_location, avg(reputation) reputation, count(review_serial) review_cnt, place_name photo_name from place a left outer join review b on a.place_serial = b.place_serial where place_code = 3
and local_code = #{_parameter}
group by place_code, local_code, a.place_serial, place_name, place_location order by place_serial

mapper.delete:
delete from like_list
where member_id = #{member_id}
and place_serial = #{place_serial}
