mapper.select:
select * from( select rownum rno, M.* from( select review_serial, member_id, place_serial, review_type, reputation, review_title, review_content, visit_date, to_char(review_date, 'rrrr-mm-dd') review_date, service, cleanliness, accessibility, price, scale, food
lpad(' ' , (level-1)*12, '&nbsp;&nbsp;' ) || subject subject, id, to_char(mdate, 'rrrr-mm-dd') mdate, hit, getAttCnt(serial) attCnt
from review where review_title like '%${findStr}%' or review_content like '%${findStr}%' connect by prior review_serial = place_serial start with pserial is null order siblings by serial desc)M ) where rno between ${startNo} and ${endNo}

select serial, id, pwd, subject, content, hit, pserial, to_char(mdate, 'rrrr-mm-dd') mDate
from board
where serial = ${_parameter}

select serial, pserial, orifile, sysfile
from boardAtt
where pserial = ${_parameter}
